mapper.select:
select

from payMoney

    1=1

        and payMoneyID=#{payMoneyID,jdbcType=BIGINT}


        and payMoneyUUID=#{payMoneyUUID,jdbcType=VARCHAR}


        and accountUUID=#{accountUUID,jdbcType=VARCHAR}


        and userUUID=#{userUUID,jdbcType=VARCHAR}


        and categoryType=#{categoryType,jdbcType=DECIMAL}


        and notify_time=#{notifyTime,jdbcType=DATE}


        and app_id=#{appId,jdbcType=VARCHAR}


        and sign_type=#{signType,jdbcType=VARCHAR}


        and trade_no=#{tradeNo,jdbcType=VARCHAR}


        and out_trade_no=#{outTradeNo,jdbcType=VARCHAR}


        and buyer_id=#{buyerId,jdbcType=VARCHAR}


        and seller_id=#{sellerId,jdbcType=VARCHAR}


        and total_amount=#{totalAmount,jdbcType=VARCHAR}


        and trade_status=#{tradeStatus,jdbcType=VARCHAR}


        and passback_params=#{passbackParams,jdbcType=VARCHAR}


        and notify_timeStr=#{notifyTimeStr,jdbcType=VARCHAR}
